document.addEventListener("DOMContentLoaded",(()=>{const t={data:()=>({text:"hello 這是要複製的文本內容"}),methods:{copyTxt(t){try{navigator.clipboard.write([new ClipboardItem({"text/html":new Blob([t],{type:"text/html"}),"text/plain":new Blob([t],{type:"text/plain"})})]),alert("文本已複製到剪貼簿")}catch(t){alert("無法複製文本："+t.message),console.error("無法複製文本：",t)}},getRef(t){const e=this.$refs[t].innerHTML;this.copyTxt(e)}},mounted(){}};Vue.createApp(t).mount("#app")}));