document.addEventListener("DOMContentLoaded",(()=>{const t={data:()=>({text:"hello 這是要複製的文本內容"}),methods:{copyTxt(t){if(navigator.clipboard)return navigator.clipboard.writeText(t).then((()=>{alert("文本已複製到剪貼簿")})).catch((t=>{alert("無法複製文本：",t),console.error("無法複製文本：",t)}))},getRef(t){const e=this.$refs[t].textContent;this.copyTxt(e)}},mounted(){}};Vue.createApp(t).mount("#app")}));